INSERT INTO userRolePermissions VALUES (1, (SELECT id FROM permissions WHERE name='plugin_audit_access'));
DELETE FROM userRolePermissions WHERE roleId=2 AND permissionId=(SELECT id FROM permissions WHERE name='plugin_audit_access');
INSERT INTO userRolePermissions VALUES (2, (SELECT id FROM permissions WHERE name='plugin_audit_access'));
